#! /usr/bin/env node

var niim;

try {
  niim = require('niim');
} catch(e) {}
if (niim) {
  console.log('niim loaded');
}

if (typeof niim === 'undefined') {
  console.log('niim not loaded', typeof niim);
  process.exit(0);
}

if (niim) {
  niim.itm(true);
  setTimeout(() => niim.itm(false), 3000);
}

